query-12.query:
PREFIX bsbm: <http://www4.wiwiss.fu-berlin.de/bizer/bsbm/v01/vocabulary/>

SELECT ?vg ?s ?type ?rating WHERE {
    GRAPH ?vg {
        ?s a ?type .
        OPTIONAL {
            ?s bsbm:rating3 ?rating .
        }
    }
}

query-13.query:
PREFIX bsbm: <http://www4.wiwiss.fu-berlin.de/bizer/bsbm/v01/vocabulary/>

SELECT ?vg ?s ?rating WHERE {
    GRAPH ?vg {
      ?s bsbm:rating3 ?rating .
      FILTER(?rating > 4)
    }
}

query-14.query:
PREFIX vers: <https://github.com/VCityTeam/ConVer-G/Version#>

SELECT (COUNT(?version) as ?countversion) WHERE {
        ?vg vers:is-in-version ?version .
}

query-15.query:
PREFIX vers: <https://github.com/VCityTeam/ConVer-G/Version#>

SELECT (COUNT(?graph) as ?countgraph) WHERE {
    ?vg vers:is-version-of ?graph .
}

query-16.query:
PREFIX building: <http://example.edu/Building#>
PREFIX schema: <http://schema.org/>
PREFIX bsbm: <http://www4.wiwiss.fu-berlin.de/bizer/bsbm/v01/vocabulary/>

SELECT ?graph (MAX(?rating) AS ?maxrating) WHERE {
    GRAPH ?graph {
      ?s bsbm:rating3 ?rating .
    }
} GROUP BY ?graph

query-17.query:
